import { Component } from '@angular/core';
import { LoginService } from 'src/app/common/login/login.service';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css']
})
export class LoginPageComponent {
  username: string;
  password: string;
  constructor(private loginService: LoginService) {
    this.username = '';
    this.password = '';
  }

  login(): void {
    if (this.username === '' || this.password === '') {

    }
    else {
      this.loginService.login(this.username, this.password)
    }
  }
}
<div class="form-container">
    <div class="form">
        <div class="form-title">Login</div>
        <div class="form-container">
            <input type="text" placeholder="username" [value]="username" (change)="username=$event.target.value">
        </div>
        <div class="form-container">
            <input type="password" placeholder="password" [value]="password" (change)="password=$event.value">
        </div>
        <div class="button-container">
            <button class="button">
                Login
            </button>
        </div>
        <div class="prompt-container">
            <div class="prompt">
                Don't have an account? <span class="prompt-hyperlink">Register</span>
            </div>
        </div>
    </div> 
</div>
